import { useState } from 'react';
import './App.css';
import Form from './components/Form';

function App() {

  const [inputText, setInputText] = useState("run");

  return (
    <div className="App">
      <header>
        <h1>My Todo List</h1>
      </header>
      <Form  inputText={inputText} setInputText={setInputText}/>
    </div>
  );
}

export default App;
